import React, { useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import AdminLayout from '../../../layouts/admin.layout';
import axios from 'axios';
import helpers from '../../../helpers/helper';
import Refunded from './refunded.component';

  const Allpayments = () =>  {
    const [payments,setPayments] = useState([]);
    const [refund,setRefund] = useState([]);
    const [paymentPage,setPaymentPage] = useState(true);
    const [refundPage,setRefundPage] = useState(false);
    const token = localStorage.getItem('token');

    const handleRefund = (e)=>{
        const charge = e.target.value;
        axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/refundPayment/${charge}`,helpers.getHeaders())
        .then((resp)=>{
            console.log(resp);
        }).catch((err)=>{
            console.log(err);
        });
    }

    useEffect (()=>{
        axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/get-payments`,helpers.getHeaders())
        .then((resp)=>{
            console.log(resp);
            setPayments(resp.data.payments);
        }).catch((err)=>{
            console.log(err);
        });
    },[]);
	return (
      <AdminLayout>
	    <div className='content'>
            {refundPage && <Refunded />}
            {paymentPage &&
            <div className='container'>
            <h2 className='text-center'>Payments</h2>
                <div className='row'>
                    <div className='col-md-10'></div>
                    <div className='col-md-2'>
                        <button className="btn btn-primary" onClick={function(){setRefundPage(true);setPaymentPage(false);}}>Refunded Payments</button>
                    </div>
                </div>
                <div className='row mt-3'>
                    <div className='col-md-3'>Amount</div>
                    <div className='col-md-3'>Amount Received</div>
                    <div className='col-md-3'>Payment Method</div>
                    <div className='col-md-3'>Action</div>
                </div>
                {
                    payments && payments.length>0 ? 
                        payments.map(item=>
                            <div className='row mt-2'>
                                <div className='col-md-3'>{item?.amount/100}</div>
                                <div className='col-md-3'>{item?.amount_received/100}</div>
                                <div className='col-md-3'>{item?.payment_method_types[0]}</div>
                                <div className='col-md-3'> <button className='btn btn-primary' onClick={handleRefund} value={item?.latest_charge}>Refund</button> </div>
                            </div>
                        )
                    :'' 
                }
            </div>
            }
	    </div>
      </AdminLayout>
	);
  }

  export default Allpayments;
