import React,{Navigate} from 'react';
import { Outlet } from 'react-router-dom';
import helpers from '../../helpers/helper';
import {Admin_Dashboard} from '../../helpers/permissions'
const Dashboard = ({permission}) =>  {
  console.log(permission);
  const userInfo = localStorage.getItem('user');
  const user = helpers.decryption(userInfo);

  //verify permission
  const verifyPermission = helpers.checkPermission(user,Admin_Dashboard);
  if(!verifyPermission) {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    <Navigate to={'/login'} />
  }
  return (
          verifyPermission? <Outlet /> : null
  );
}

export default Dashboard;
