import React, { useState,useEffect } from 'react';
import { Link,useNavigate } from 'react-router-dom';
import AdminLayout from '../../../layouts/admin.layout';
import axios from 'axios';
import helpers from '../../../helpers/helper';

  const Allpayments = () =>  {
    const [orders,setOrders] = useState([]);
    const navigate = useNavigate();
    const token = localStorage.getItem('token');

    const handleRefund = (e)=>{
        const charge = e.target.value;
        axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/refundPayment/${charge}`,helpers.getHeaders())
        .then((resp)=>{
            console.log(resp);
            navigate('/refunded-payments')
        }).catch((err)=>{
            console.log(err);
        });
    }

    useEffect (()=>{
        axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/get-payments`,helpers.getHeaders())
        .then((resp)=>{
            setOrders(resp.data.orders);
            console.log(resp.data.orders);
        }).catch((err)=>{
            console.log(err);
        });
    },[]);
	return (
      <AdminLayout>
	    <div className='content'>
            <div className='container'>
            <h2 className='text-center'>Orders</h2>
                <div className='row'>
                    <div className='col-md-10'></div>
                    <div className='col-md-2'>
                        <Link className="btn btn-primary" to='/refunded-payments'>Refunded Payments</Link>
                    </div>
                </div>
                <div className='row mt-3'>
                    <div className='col-md-1'>Order No.</div>
                    <div className='col-md-1'>Order Amount</div>
                    <div className='col-md-1'>Amount Received</div>
                    <div className='col-md-2'>User Name</div>
                    <div className='col-md-2'>User Email</div>
                    <div className='col-md-2'>Payment Status</div>
                    <div className='col-md-1'>Action</div>
                </div>
                {
                    orders && orders.length>0 ? 
                        orders.map(item=>
                            <div className='row mt-2'>
                                <div className='col-md-1'>{item?.id}</div>
                                <div className='col-md-1'>${item?.total_amount}</div>
                                <div className='col-md-1'>${item?.payment_received}</div>
                                <div className='col-md-2'>{item?.user?.name}</div>
                                <div className='col-md-2'>{item?.user?.email}</div>
                                <div className='col-md-2'>{item?.payment_status}</div> 
                                <div className='col-md-1'> 
                                    <button className='btn btn-primary' onClick={handleRefund} value={item?.id}>Refund</button> 
                                    </div>
                            </div>
                        )
                    :'' 
                }
            </div>
	    </div>
      </AdminLayout>
	);
  }

  export default Allpayments;
