import React from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
const initialOptions = {
    clientId: "AXzXikAFC3jt6oOOE6VqajfrUF3wX-gVcd7DA4wan26noU8uG2cVeElNa60kcWR-PJUgIIZ__LG0W3Y7",
    currency: "USD",
    intent: "capture",
};
function createOrder() {
    // axios.get(`${process.env.REACT_APP_USER_BASE_URL}/get-user-products`,{headers :header})
    // .then((response)=>{
    //     setUserproducts(response.data.products);
    //     setTotalAmt(response.data.totalAmt);
    //     setQuantity(response.data.quantity);
    // }).catch((err)=>{
    //     console.log(err);
    // });
    const token =  localStorage.getItem('token');
    const header = {Authorization:`Bearer ${token}`};
    return fetch(`${process.env.REACT_APP_USER_BASE_URL}/get-user-products`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            header
        },
        // use the "body" param to optionally pass additional order information
        // like product ids and quantities
        body: JSON.stringify({
            cart: [
                {
                    id: "YOUR_PRODUCT_ID",
                    quantity: "YOUR_PRODUCT_QUANTITY",
                },
            ],
        }),
    })
        .then((response) => response.json())
        .then((order) => order.id);
}
const Payment = () =>  {
    return (
        <div key={1}>
            <PayPalScriptProvider options={initialOptions}>
                <PayPalButtons />
            </PayPalScriptProvider>
        </div>
    );
}

  export default Payment;
