import React,{useEffect,useState} from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import helpers from '../../../helpers/helper';
import axios from 'axios';

  const Cart = ({amount}) =>  {
    const stripe = useStripe();
    const elements = useElements();
    const [error, setError] = useState(null);
    const [userproducts,setUserproducts] = useState(null);

    const handleSubmit = async(e) =>{
        e.preventDefault();
        if (!stripe || !elements) {
            return;
        }
        const cardElement = elements.getElement(CardElement);

        try {
            const { token } = await stripe.createToken(cardElement);
            const userToken = localStorage.getItem('token');
            const response = await fetch('/payment', {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                  'Authorization' : `Bearer ${userToken}`
                },
                body: JSON.stringify({
                  amount: amount * 100, // Amount in cents
                  token,
                }),
            });
            if (response.ok) {
                console.log('Payment Successful!');
              } else {
                setError('Payment failed. Please try again.');
            }
        }catch(error)
        {
            setError(error.message);
        }
    }

    useEffect(()=>{
        const user = helpers.getCurrentUser();
        const token =  localStorage.getItem('token');
        const header = {Authorization:`Bearer ${token}`}
        axios.get(`${process.env.REACT_APP_USER_BASE_URL}/get-user-products/${user.id}`,{headers :header})
        .then((response)=>{
            setUserproducts(response.data.products)
        }).catch((err)=>{
            console.log(err);
        });
    },[]);
	return (
	  <div>
            <div className='container'>
                <h2 className='text-center'>Cart</h2>
                <div className='row'>
                    <div className='col-md-3'>Name</div>
                    <div className='col-md-3'>Price</div>
                    <div className='col-md-3'>Quantity</div>
                    <div className='col-md-3'>Total</div>
                </div>
                {
                    userproducts? 
                    userproducts?.products?.map(item=>
                        <div className='row'>
                            <div className='col-md-3'>{item?.name}</div>
                            <div className='col-md-3'>{item?.price}</div>
                            <div className='col-md-3'>{item?.quantity}</div>
                            <div className='col-md-3'>{item?.price * item?.quantity}</div>
                        </div>
                        )
                    :''
                }
                <hr />
                <div className='row'>
                    <div className='col-md-9'>Total</div>
                    <div className='col-md-3'>12000</div>
                </div>
                {/* <a href=''>Pay</a> */}
                <button onClick={handleSubmit} disabled={!stripe}>Pay</button>
                {error && <div>{error}</div>}
            </div>
	  </div>
	);
  }

export default Cart;
