import React,{useEffect,useState} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import UserLayout from '../../layouts/user.layout';
import helpers from '../../helpers/helper.js';

const UserDashboard = () =>  {
	const [products,setProducts] = useState([]);

	const handleAddToCart = (e) =>{
		const id = e.target.value;
		axios.get(`${process.env.REACT_APP_USER_BASE_URL}/add-to-cart/${id}`,helpers.getHeaders())
		.then((response)=>{	
			console.log(response);
		}).catch((err)=>{
			console.log(err);
		});
	}
	useEffect (()=>{
		const token = localStorage.getItem('token');
		const header = {Authorization:`Bearer ${token}`};
		axios.get(`${process.env.REACT_APP_USER_BASE_URL}/get-products`,{headers:header})
		.then((response)=>{
			setProducts(response.data.products);
		}).catch((err)=>{
			console.log(err);
		});
	},[]);
	return (
		<UserLayout>
			<div>
				<div className='container'>
					<h2 className='text-center'>Products</h2>
					<div className='row'>
						<div className='col-md-10'></div>
						<div className='col-md-2'>
							<Link to='/cart'>My cart</Link>
						</div>
					</div>
					{/* <div className='row'>
						<div className='col-md-3'>Name</div>
						<div className='col-md-3'>Price</div>
						<div className='col-md-3'>Quantity Left</div>
						<div className='col-md-3'>Add to cart</div>
					</div> */}
					<div className='row mt-2'>
						{products && products.length>0? 
							products?.map(item =>
								<div className='col-md-4 mt-4'>
									<p>{item?.name}</p>
									<p>Price: {item?.price}</p>
									<p>Quantity left: {item?.quantity}</p>
									<p> {item?.quantity>0 ? <button className='btn btn-success' onClick={handleAddToCart} value={item?.id}> Add to Cart +</button> : <span style={{color:'green'}}>Out of Stock</span>}</p>
								</div>

							)
						:''
						}	
					</div>
				</div>
			</div>
	  </UserLayout>
	);
}

export default UserDashboard;
