import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { GoogleLogin,googleLogout, useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import helpers from '../helpers/helper.js';
import {Admin_Dashboard,User_Dashboard} from '../helpers/permissions.js';
// const baseURL = "http://localhost:5000/public";

function Login() {
  const navigate = useNavigate();
  const keys = ({email:'',password:''});
  const [formData,setFormData] = useState(keys);

  //after successfully email login
  const responseMessage = (response) => {
    //login with google
    axios.post(`${baseURL}/google-auth`,response).then((resp) => {
      //set token in local storage
      localStorage.setItem('token',resp.data.token);
      navigate('/dashboard');
    }).catch((err) => console.log('errors',err));
  };

  //after error from email
  const errorMessage = (error) => {
      console.log(error);
      alert('Something went wrong');
  };

  //set form data
  const handleChange = (e)=>{
    const {name,value} = e.target;
    setFormData({...formData,[name]:value});
  }

  //Login
  const handleSubmit = (e) => {
    axios.post(`${process.env.REACT_APP_PUBLIC_BASE_URL}/login`,formData)
    .then((response)=>{
      localStorage.setItem('token',response.data.token);
      const user = helpers.decryption(response.data.user);
      if(helpers.checkPermission(user,Admin_Dashboard))
      {
        navigate('/admin-dashboard');
      }
      else if(helpers.checkPermission(user,User_Dashboard))
      {
        navigate('/user-dashboard');
      }
    }).catch((err)=>{
      console.log(err);
    });
  }

  useEffect(
    () => {
      // if(localStorage.getItem('token')) navigate('/dashboard');
    },
    []
  );

  return (
    <div className='container w-50'>
        <div className='container'>
          <div className='row mt-5'>
            <label> Email</label>
            <input type='text' name="email" placeholder='Enter your email' onChange={handleChange}/>
          </div>
          <div className='row mt-3'>
            <label> Password </label>
            <input type='text' name="password" placeholder='Enter your password' onChange={handleChange}/>
          </div>
          <button className='m-2' onClick={handleSubmit}>Login</button>
          <button className='m-2 btn btn-primary'>Sign in with Microsoft</button>
           <GoogleLogin onSuccess={responseMessage} onError={errorMessage} />
        </div>
    </div>
  );
}
export default Login;
