import React, { useState,useEffect } from 'react';
import AdminLayout from '../../../layouts/admin.layout';
import axios from 'axios';
import helpers from '../../../helpers/helper';
import Swal from 'sweetalert2';
import Order from './orders.component';

const Allpayments = () =>  {
    const [orders,setOrders] = useState([]);
    const [searchOrder,setSearchOrder] = useState([]);

    //for pagination
    const [loading, setLoading] = useState(false);
    const [currentPage, setCurrentPage] = useState(2);
    const [ordersPerPage, setOrdersPerPage] = useState(3);

    const handleFindProduct = (e) =>{
        const item_id = e.target.value;
        // Swal.fire('Please fill authorized card information', '', 'error');
    }
    //refund functionality
    const handleRefund = (e)=>{
        Swal.fire({
            title: 'Do you want to Refund the money?',
            showDenyButton: true,
            confirmButtonText: 'Yes',
            denyButtonText: 'No',
            customClass: {
              actions: 'my-actions',
              confirmButton: 'order-2',
              denyButton: 'order-3',
            },
          }).then((result) => {
            if (result.isConfirmed) {
            const Ordercharge = e.target.value;
                axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/refundPayment/${Ordercharge}`,helpers.getHeaders())
                .then((resp)=>{
                    console.log(resp);
                    Swal.fire('Successfully Refund', '', 'success').then((resp)=>{
                        // navigate('/refunded-payments')
                        getAllOrders();
                    });
                }).catch((err)=>{
                    console.log(err);
                });
            } else if (result.isDenied) {
                // Swal.fire("", '', 'info')
            }
          });
    }
    //get all orders
    const getAllOrders = () =>{
        axios.get(`${process.env.REACT_APP_ADMIN_BASE_URL}/get-payments`,helpers.getHeaders())
        .then((resp)=>{
            setOrders(resp.data.orders);
            paginateOrders();
            // console.log(resp.data.orders);
        }).catch((err)=>{
            console.log(err);
        });
    }
    //for filteration
    const searchOrders = (e) =>{
        const {value} = e.target.value;
        setSearchOrder(searchOrder);
    }
    //for pagination
    const paginateOrders =() => {
        const indexLastItem = currentPage * ordersPerPage;
        const indexFirstItem = indexLastItem-ordersPerPage;
        console.log(indexLastItem,indexFirstItem);
    }

    useEffect (()=>{
        getAllOrders();
    },[]);
	return (
      <AdminLayout>
	    <div className='content'>
            <div className='container'>
            <h2 className='text-center'>Orders</h2>
            <div className='row'>
                <div className='col-md-9'></div>
                <div className='col-md-3'>
                    <input type='text' name="search" placeholder='search' onChange={(e)=>searchOrders(e)}/>
                </div>
            </div>
                <div className='row'>
                    <div className='col-md-10'></div>
                    <div className='col-md-2'>
                    </div>
                </div>
                <Order orders={orders} handleFindProduct={handleFindProduct} handleRefund={handleRefund}/>
            </div>
	    </div>
      </AdminLayout>
	);
}

export default Allpayments;
