import React from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
const initialOptions = {
    clientId: "AXzXikAFC3jt6oOOE6VqajfrUF3wX-gVcd7DA4wan26noU8uG2cVeElNa60kcWR-PJUgIIZ__LG0W3Y7",
    currency: "USD",
    intent: "capture",
};

const Payment = () =>  {
    function createOrder() {

        console.log(header);
        return fetch(`${process.env.REACT_APP_USER_BASE_URL}/create-payment-paypal`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                header
            },
            // use the "body" param to optionally pass additional order information
            // like product ids and quantities
            body: JSON.stringify({
                cart: [
                    {
                        id: 1,
                        quantity: 2,
                    },
                ],
            }),
        })
            .then((response) => response.json())
            .then((order) => order.id);
    }
    function onApprove(data) {
        return fetch(`${process.env.REACT_APP_USER_BASE_URL}/capture-paypal-order`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            orderID: data.orderID
          })
        })
        .then((response) => response.json())
        .then((orderData) => {
              const name = orderData.payer.name.given_name;
              alert(`Transaction completed by ${name}`);
        });

    }

    return (
        <div key={1}>
            <PayPalScriptProvider options={initialOptions}>
                <PayPalButtons  onApprove={onApprove}/>
            </PayPalScriptProvider>
        </div>
    );
}

  export default Payment;
