import React,{Navigate} from 'react';
import { Outlet } from 'react-router-dom';
import helpers from '../../helpers/helper';
import {Admin_Dashboard} from '../../helpers/permissions'
  const AdminRoutes = () =>  {
    const userInfo = localStorage.getItem('user');
    const user = helpers.decryption(userInfo);

    //verify permission
    const permission = helpers.checkPermission(user,Admin_Dashboard);
    if(!permission) <Navigate to={'/login'} />
	return (
            permission? <Outlet /> : null
	);
  }

  export default AdminRoutes;
