import React from 'react';
import { Outlet ,useNavigate,useEffect } from 'react-router-dom';
import helpers from '../../helpers/helper';
import { Admin_Dashboard } from '../../helpers/permissions';
const Dashboard = ({permission}) =>  {
  const userInfo = localStorage.getItem('user');
  const user = helpers.decryption(userInfo);
  const navigate = useNavigate();

  useEffect = () => {
  // //verify permission
  const verifyPermission = helpers.checkPermission(user,permission);
  if(!verifyPermission) {
  console.log('yes');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login')
  }
  }
  return (
       verifyPermission? <Outlet /> : null
  );
}

export default Dashboard;
