import React,{useEffect,useState} from 'react';
import helpers from '../../../helpers/helper';
import axios from 'axios';

  const Cart = () =>  {
    const [userproducts,setUserproducts] = useState(null);
    useEffect(()=>{
        const user = helpers.getCurrentUser();
        const token =  localStorage.getItem('token');
        const header = {Authorization:`Bearer ${token}`}
        axios.get(`${process.env.REACT_APP_USER_BASE_URL}/get-user-products/${user.id}`,header)
        .then((response)=>{
            // console.log(response);
            setUserproducts(response.data.products)
        }).catch((err)=>{
            console.log(err);
        });
    });
	return (
	  <div>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-3'>Name</div>
                    <div className='col-md-3'>Price</div>
                    <div className='col-md-3'>Quantity</div>
                    <div className='col-md-3'>Total</div>
                </div>
                {
                    userproducts? 
                    userproducts?.products?.map(item=>
                        <div className='row'>
                            <div className='col-md-3'>{item?.name}</div>
                            <div className='col-md-3'>{item?.price}</div>
                            <div className='col-md-3'>{item?.quantity}</div>
                            <div className='col-md-3'>{item?.price * item?.quantity}</div>
                        </div>
                        )
                    :''
                }
            </div>
	  </div>
	);
  }

export default Cart;
